const chai = require('chai');
const chaiHttp = require('chai-http');
const app = require('../server'); // Assuming your server file is one directory above the test folder
const expect = chai.expect;

// Configure chai
chai.use(chaiHttp);
import('chai').then(chai => {
    // Use chai here
  }).catch(error => {
    // Handle error
    console.error('Error while importing chai:', error);
  });
  
// Test cases for API endpoints
describe('API Tests', () => {
  describe('GET /', () => {
    it('should return status 201', (done) => {
      chai.request(app)
        .get('/')
        .end((err, res) => {
          expect(res).to.have.status(200);
          done();
        });
    });
  });

  describe('POST /api/data', () => {
    it('should insert data and return status 201', (done) => {
      chai.request(app)
        .post('/api/data')
        .send({ message: 'Test data' })
        .end((err, res) => {
          expect(res).to.have.status(200);
          expect(res.body).to.have.property('message').equal('Data received successfully');
          done();
        });
    });
  });

  // Add more test cases for other API endpoints if needed
});
